/**
* チームメンバーの状況を更新するアクション名
*/
export const UPDATE_MEMBER_STATUS = 'UPDATE_MEMBER_STATUS';

/**
* チームメンバーの状況を更新するアクションのcreator
* @param {Object} memberStatus メンバー状況
* @return {Object} UPDATE_MEMBER_STATUSアクション
*/
export function updateMemberStatus(memberStatus){
  return {
    type:UPDATE_MEMBER_STATUS,
    payload:memberStatus
  }
}

export const CLEAR_MY_DESTINATION='CLEAR_MY_DESTINATION';

/**
* ログイン要求アクション名
*/
export const LOGIN_REQUESTED = "LOGIN_REQUESTED";

/**
* ログイン要求アクションのcreator
* @param {string} id ユーザーID
* @param {string} pass パスワード
* @return {Object} ログイン要求アクション
*/
export function loginRequested(id,pass){
  return {
    type:LOGIN_REQUESTED,
    payload:{
      id:id,
      pass:pass
    }
  };
}

/**
* ログイン成功アクション名
*/
export const LOGIN_SUCCESS = "LOGIN_SUCCESS";

/**
* ログイン成功アクションのcreator
* @param {{userid:string,name:string,token:string}} userinfo 成功したユーザー情報
* @return {Object} ログイン成功アクション
*/
export function loginSuccess(userinfo){
  return {
    type:LOGIN_SUCCESS,
    payload:userinfo
  };
}

/**
* ログイン失敗アクション名
*/
export const LOGIN_FAILURE = "LOGIN_FAILURE";

/**
* ログイン失敗アクションのcreator
* @return {Object} ログイン失敗アクション
*/
export function loginFailure(){
  return {
    type: LOGIN_FAILURE
  };
}

/**
* ログアウト要求アクション名
*/
export const LOGOUT_REQUESTED = "LOGOUT_REQUESTED";

/**
* ログアウト要求アクションのcreator
* @return {Object} ログアウト要求アクション
*/
export function logoutRequested(){
  return {
    type : LOGOUT_REQUESTED
  };
}

/**
* 自分の行き先変更アクション名
*/
export const MY_DESTINATION_CHANGE = "MY_DESTINATION_CHANGE";

/**
* 自分の行き先変更アクションのcreator
* @param {{inBusiness:boolean,comment:string}} destination 行き先
* @return {Object} 自分の行き先変更アクション
*/
export function myDestinationChange(destination){
  return {
    type:MY_DESTINATION_CHANGE,
    payload:destination
  };
}

/**
* 自分の行き先クリアアクション名
*/
export const MY_DESTINATION_CLEAR = "MY_DESTINATION_CLEAR";

/**
* 自分の行き先クリアアクションのcreator
* @return {Object} 自分の行き先クリアアクション
*/
export function myDestinationClear(){
  return {
    type:MY_DESTINATION_CLEAR
  };
}

/**
*サインアップ要求アクション名
*/
export const SIGNUP_REQUIRED = "SIGNUP_REQUIRED";

/**
* サインアップ要求アクションのcreator
* @param {{id:string,password:string,name:string}} userinfo 登録を要求するユーザー情報
* @return {Object} SIGNUP_REQUIREDアクション
