      <button onClick={this.hundleClick}>logout</button> :
      null
    return(
      <div>キョウ-ドコ？&nbsp;{logoutButton}</div>
    )
  }
}

import React from 'react'
import { connect } from 'react-redux'
import Header from './header'
import LoginForm from './loginform'
import SignUpForm from './signupform'
import Board from './board'
import MyDestination from './mydestination'
import {LOGIN_STATUS} from '../const/login'


/**
*メインコンポーネント
* @see http://qiita.com/nownabe/items/2d8b92d95186c3941de0
*/
class Kyodoco extends React.Component{
  /**
  * コンストラクタ
  * @param {Object} props プロパティ
  * @return {undefined}
  */
  constructor(props){
    super(props);
  }

  /**
  * 描画メソッド
  * @return {undefined}
  */
  render(){
    //ログインしていればメイン画面を、そうでなければログインフォームを描画する
    const content =
      this.props.login.status == LOGIN_STATUS.SUCCESS ?
      (<div>
        <MyDestination
          dispatch={this.props.dispatch}
          login={this.props.login}
          mydestination={this.props.mydestination} />
        <Board
          memberStatus={this.props.board.memberStatus}
        />
       </div>
      ) :
      (
        <div>
          <LoginForm
          dispatch={this.props.dispatch}
          login={this.props.login}/>
          <SignUpForm
          dispatch={this.props.dispatch}
          signup={this.props.signup}/>
        </div>
      );

    return(
      <div>
      <header>
        <Header dispatch={this.props.dispatch} login={this.props.login}/>
      </header>
      <hr />
      {content}
      </div>
    );
  }
}
/**
* reduxのstore上のstateをcomponentに接続する関数
* @see http://qiita.com/kompiro/items/7fe90c4abc92fd32b343
*/
function mapStateToProp(state){
  return state;
}

/**
* メインコンポーネントをstoreと接続してexportする
* @see http://qiita.com/nownabe/items/2d8b92d95186c3941de0
* @see http://qiita.com/kompiro/items/7fe90c4abc92fd32b343
*/
export default connect(mapStateToProp)(Kyodoco);

import React from 'react'
import ReactDOM from 'react-dom'
import {loginRequested} from '../action/login'
import {LOGIN_STATUS} from '../const/login'
import TextField from 'material-ui/TextField'
import RaisedButton from 'material-ui/RaisedButton'

/**
* LoginFormコンポーネント
* @see http://qiita.com/nownabe/items/2d8b92d95186c3941de0
*/
export default class LoginForm extends React.Component{
  /**
  * コンストラクタ
  * @param {Object} props プロパティ
  * @return {undefined}
  */
  constructor(props){
    super(props);
    this.state = {current_id_text:"",current_pass_text:""};
    //ES2015版のReactだとこのおまじないをしないとメソッド内でthisが解決しない...
    this.hundleSubmit = this.hundleSubmit.bind(this);
    this.hundleIDChange = this.hundleIDChange.bind(this);
    this.hundlePassChange = this.hundlePassChange.bind(this);
  }

  /**
  * ログインボタンのクリックをハンドリングするメソッド
  * @return {undefined}
  */
  hundleSubmit(){
    /*
    *入力されたID/Passを取得して、
    *それを元にLOGIN_REQUESTED(ログイン要求)アクションを作成してdispatchする
    */
    const userid = this.state.current_id_text.trim();
    const password = this.state.current_pass_text.trim();
    if(userid && password){
      this.props.dispatch(loginRequested(userid,password));
    }
  }

  hundleIDChange(event,newValue){
    this.setState({current_id_text:newValue});
  }

  hundlePassChange(event,newValue){
    this.setState({current_pass_text:newValue});
  }

  /**
  * 描画メソッド
  * @return {undefined}
  */
  render(){
    //現在の状態がLOGIN_STATUS.FAILURE(ログイン失敗)であれば失敗メッセージを表示する
    const message = this.props.login.status == LOGIN_STATUS.FAILURE ?
      "IDもしくはパスワードが正しくありません" :
      "";

    return (
      <div>
        <h4>ログイン</h4>
        <TextField
        hintText="User ID"
        floatingLabelText="User ID"
        value={this.state.current_id_text}
        ref="userid"
        onChange={this.hundleIDChange}
        />
        <br />
        <TextField
          hintText="Password"
          floatingLabelText="Password"
          type="password"
          value={this.state.current_pass_text}
          ref="password"
          onChange={this.hundlePassChange}
          />
        <br />
        <br />
        <RaisedButton onClick={this.hundleSubmit} label="Login"/>
        <br />
        <span style={{color:"red"}}>{message}</span>
      </div>
    );
  }
}

import React from 'react'
import ReactDOM from 'react-dom'
import {myDestinationChange,myDestinationClear} from '../action/mydestination'
import Toggle from 'material-ui/Toggle'
import TextField from 'material-ui/TextField'

/**
* "自分の行き先"コンポーネント
* @see http://qiita.com/nownabe/items/2d8b92d95186c3941de0
*/
export default class MyDestination extends React.Component{
  /**
  * コンストラクタ
  * @param {Object} props
  * @return {undefined}
  */
  constructor(props){
    super(props);
    console.log(props.mydestination.inBusiness);
    this.state = {
      currentInBusiness:!props.mydestination.inBusiness,
      currentComment:props.mydestination.comment
    };

    //ES2015版のReactだとこのおまじないをしないとメソッド内でthisが解決しない...
    this.hundleChange = this.hundleChange.bind(this);
    this.hundleClear = this.hundleClear.bind(this);
    this.hundleToggle = this.hundleToggle.bind(this);
    this.hundleTextChange = this.hundleTextChange.bind(this);
  }
  /**
  * 行き先の変更をハンドリングするメソッド
  * @return undefined
  */
  hundleChange(){
    this.props.dispatch(myDestinationChange({inBusiness:!this.state.currentInBusiness, comment:this.state.currentComment}));
  }

  hundleClear(){
    const action = myDestinationClear();
    this.props.dispatch(action);
  }

  hundleToggle(event,isInputChecked){
    this.setState({currentInBusiness : isInputChecked});
    this.hundleChange();
  }

  hundleTextChange(event,newValue){
    this.setState({currentComment:newValue});
    this.hundleChange();
  }

  /**
  * 描画メソッド
  * @return {undefined}
  */
  render(){
    const inBusinessValue = this.props.mydestination.inBusiness ? 1 : 0;
    const commentValue = this.props.mydestination.comment;
    return (
      <div>
      <h3>自分の行き先</h3>
      <table>
        <tbody>
        <tr>
          <td>{this.props.login.user.name}</td>
          <td>
            <Toggle label="出勤" toggled={this.state.currentInBusiness} onToggle={this.hundleToggle} />
          </td>
          <td>
            <TextField hintText="コメント" value={this.state.currentComment} onChange={this.hundleTextChange}/>
          </td>
          <td>
            <button onClick={this.hundleClear}>Clear</button>
          </td>
        </tr>
        </tbody>
      </table>
      </div>);
  }
}

import React from 'react'
import ReactDOM from 'react-dom'
import {signupFailure,signupRequired} from '../action/signup'
import {SIGNUP_FAILURE_REASONS} from '../const/signup'
import TextField from 'material-ui/TextField'
import RaisedButton from 'material-ui/RaisedButton'
/**
* サインアップフォームのComponent
* @see http://qiita.com/nownabe/items/2d8b92d95186c3941de0
*/
export default class SignUpForm extends React.Component{
  /**
  * コンストラクタ
  * @param {Object} props
  * @return {undefined}
  */
  constructor(props){
    super(props);
    this.state = {id_text:"",pass_text:"",confirm_pass_text:"",name_text:""};
    //ES2015版のReactだとこのおまじないをしないとメソッド内でthisが解決しない...
    this.hundleSubmit = this.hundleSubmit.bind(this);
    this.validate = this.validate.bind(this);
    this.hundleIDChange = this.hundleIDChange.bind(this);
    this.hundlePassChange = this.hundlePassChange.bind(this);
    this.hundleConfirmPasswordChange = this.hundleConfirmPasswordChange.bind(this);
    this.hundleNameChange = this.hundleNameChange.bind(this);
  }
  /**
  * Sign Upボタンのクリックをハンドリングするメソッド
  * @return {undefined}
  */
  hundleSubmit(){
    const id = this.state.id_text.trim();
    const pass = this.state.pass_text.trim();
    const pass_confirm = this.state.confirm_pass_text.trim();
    const name = this.state.name_text.trim();

    //入力値の画面サイドバリデーション
    const forminfo = {id:id,pass:pass,pass_confirm:pass_confirm,name:name};
    const validationErrors = this.validate(forminfo);


    if(validationErrors.length > 0){
      //画面サイドバリデーションが通らないので失敗
      this.props.dispatch(signupFailure(validationErrors));
    }else{
      //画面サイドバリデーションが通ったので、サインアップ要求する
      this.props.dispatch(signupRequired({id:id,password:pass,name:name}));
    }

  }

  // handle郡
  hundleIDChange(event,newValue){
    this.setState({id_text:newValue});
  }

  hundlePassChange(event,newValue){
    this.setState({pass_text:newValue});
  }

  hundleConfirmPasswordChange(event,newValue){
    this.setState({confirm_pass_text:newValue});
  }

  hundleNameChange(event,newValue){
    this.setState({name_text:newValue});
  }

  /**
  * フォームデータのバリデーション
  * @param {{id:string,pass:string,pass_confirm:string,name:string}} forminfo フォームデータ
  * @return {array} SIGNUP_FAILURE_REASONSの配列
  */
  validate(forminfo){
    const errors = [];

    if(!forminfo.id || forminfo.id.trim().length == 0){
      errors.push(SIGNUP_FAILURE_REASONS.EMPTY_ID);
    }
    if(!forminfo.pass || forminfo.pass.trim().length == 0){
      errors.push(SIGNUP_FAILURE_REASONS.EMPTY_PASSWORD);
    }
    if(!forminfo.name || forminfo.name.trim().length == 0){
      errors.push(SIGNUP_FAILURE_REASONS.EMPTY_NAME);
    }
    if(forminfo.pass !== forminfo.pass_confirm){
      errors.push(SIGNUP_FAILURE_REASONS.INVALID_CONFIRM);
    }
    return errors;

  }
  /**
  * 描画メソッド
  * @return {undefined}
  */
  render(){
    const failure_reason = this.props.signup.failure_reason;
    const emptyIdMessage = failure_reason.indexOf(SIGNUP_FAILURE_REASONS.EMPTY_ID) >= 0 ?
      "IDを入力してください" : "";
    const emptyPassMessage = failure_reason.indexOf(SIGNUP_FAILURE_REASONS.EMPTY_PASSWORD) >= 0 ?
      "パスワードを入力してください" : "";
    const emptyNameMessage = failure_reason.indexOf(SIGNUP_FAILURE_REASONS.EMPTY_NAME) >= 0 ?
      "名前を入力してください" : "";
    const invalidConfirmMessage = failure_reason.indexOf(SIGNUP_FAILURE_REASONS.INVALID_CONFIRM) >= 0 ?
      "パスワードとパスワード（確認）が一致しません" : "";
    const idDuplicatedMessage = failure_reason.indexOf(SIGNUP_FAILURE_REASONS.ID_DUPLICATED) >= 0 ?
      "ご指定のIDはすでに使われています" : "";
    const serverErrorMessage = failure_reason.indexOf(SIGNUP_FAILURE_REASONS.SERVER_ERROR) >= 0 ?
      "サーバーエラーが発生しました" : "";

    return (
      <div>
        <h4>ユーザー登録</h4>
        <TextField
        hintText="User ID"
        floatingLabelText="User ID"
        value={this.state.id_text}
        ref="id"
        onChange={this.hundleIDChange}
        />
        <br />
        <span style={{color:"red"}}>
          {emptyIdMessage}{idDuplicatedMessage}
        </span>
        <br />
        <TextField
        hintText="Password"
        floatingLabelText="Password"
        value={this.state.pass_text}
        ref="pass"
        type="password"
        onChange={this.hundlePassChange}
        />
        <br />
        <span style={{color:"red"}}>
          {emptyPassMessage}
        </span>
        <br />
        <TextField
        hintText="Password(Confirm)"
        floatingLabelText="Password(Confirm)"
        value={this.state.confirm_pass_text}
        ref="pass_confirm"
        type="password"
        onChange={this.hundleConfirmPasswordChange}
        />
        <br />
        <span style={{color:"red"}}>
          {invalidConfirmMessage}
        </span>
        <br />
        <TextField
        hintText="表示名"
        floatingLabelText="表示名"
        value={this.state.name_text}
        ref="name"
        onChange={this.hundleNameChange}
        />
        <br />
        <span style={{color:"red"}}>
          {emptyNameMessage}
        </span>
        <br />
        <br />
        <RaisedButton onClick={this.hundleSubmit} label="Sign Up"/>
      </div>
    )
  }
}

/**
* login state内のstatus定義。SUCCESS(成功)、FAILURE(失敗)、NOTYET(未実施)。
*/
export const LOGIN_STATUS={
  SUCCESS:"SUCCESS",
  FAILURE:"FAILURE",
  NOTYET:"NOTYET"
}

/**
* サインアップ失敗理由
*/
export const SIGNUP_FAILURE_REASONS = {
  ID_DUPLICATED : "ID_DUPLICATED", //ID重複
  SERVER_ERROR : "SERVER_ERROR", //サーバーエラー
  INVALID_CONFIRM : "INVALID_CONFIRM", //パスワード確認用不一致
  EMPTY_ID : "EMPTY_ID", //IDが空,
  EMPTY_PASSWORD:"EMPTY_PASSWORD", //パスワードが空
  EMPTY_NAME : "EMPTY_NAME" //表示名が空
}

